import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { observer } from 'mobx-react';
import { flowResult } from 'mobx';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faLock, faUserAlt, faUserSecret } from '@fortawesome/free-solid-svg-icons';
import { useForm, SubmitHandler } from 'react-hook-form';
import { theme } from '../../style/theme';
import authRepository from '../../repository/authRepository';
import userStore from '../../store/userStore';
import { ISignUpData } from '../../interfaces';
import { zoomIn } from '../../style/keyframes';


const SignUpBox: React.FC<{ submit: () => void }> = observer(({ submit }) => {
    const { register, handleSubmit, watch, setError, trigger, formState: { errors } } = useForm<ISignUpData>();
    const [ isChecked, setIsChecked ] = useState<boolean>(false); // 계정 중복체크

    // 회원가입
    const signUp = async (data: ISignUpData) => {
        try{
            const isSuccess = await flowResult(authRepository.signUp(data));
            if (isSuccess) {
                submit();
            };
        } catch(err) {
            alert('서버 점검중입니다');
        };
    };

    // 중복체크
    const accountCheck = async () => {
        await trigger('account'); // 트리거 호출해서 에러 확인
        if (errors.account) { // 아이디 형식에 문제가 있으면 리턴
            return
        } else {
            const account = watch('account');
            const isAvailable = await flowResult(userStore.checkAccount(account))
            if (isAvailable) {
                return setIsChecked(true);
            } else {
                setError('account', {
                    message: '이미 사용중인 아이디입니다'
                });
                setIsChecked(false);
            };
        };
    };

    const onSubmit: SubmitHandler<ISignUpData> = (data) => {
        if (!isChecked) {
            alert('중복확인을 해주세요');
            return;
        };
        signUp(data);
    };

    useEffect(() => {
        setIsChecked(false);
    }, [watch('account')]); // eslint-disable-line

    return (
        <form onSubmit={handleSubmit(onSubmit)} >
        <Box>
            <Column>
                <Row>
                    <Icon
                        icon={faUserAlt}
                        color={errors.account ? (theme.colors.red) : (theme.colors.white)}
                    />
                    <Input
                        placeholder='아이디'
                        {...register('account', {
                            required: '아이디를 입력해주세요',
                            pattern: { value: /^[a-zA-Z0-9]+$/, message: '영문과 숫자만을 조합하여 입력해주세요'},
                            minLength: { value: 6, message: '아이디는 최소 6자리입니다' },
                            maxLength: { value: 15, message: '아이디는 최대 15자리입니다' },
                        })}
                    />
                    <CheckAccountButton
                        onClick={accountCheck}
                        type='button'
                    >
                        중복확인
                    </CheckAccountButton>
                </Row>
                {!isChecked ? (!errors.account ? <Msg>ㅤ</Msg> : <ErrorMsg> {errors.account.message} </ErrorMsg>) : <Msg style={{ color: theme.colors.green }}> 사용가능한 아이디입니다 </Msg> }
            </Column>
            <Column>
                <Row>
                    <Icon
                        icon={faLock}
                        color={errors.password ? (theme.colors.red) : (theme.colors.white)}
                    />

                    <Input
                        placeholder='비밀번호'
                        autoComplete='off'
                        type='password'
                        {...register('password', {
                            required: '비밀번호를 입력해주세요',
                            minLength: { value: 8, message: '비밀번호는 최소 8자리입니다' },
                            maxLength: { value: 20, message: '비밀번호는 최대 20자리입니다' }
                        })}
                    />
                </Row>
                {errors.password ? <ErrorMsg> {errors.password.message} </ErrorMsg> : <Msg>ㅤ</Msg>}
            </Column>
            <Column>
                <Row>
                    <Icon
                        icon={faCheck}
                        color={errors.check ? (theme.colors.red) : (theme.colors.white)}
                    />
                    <Input
                        placeholder='비밀번호 확인'
                        autoComplete='off'
                        type='password'
                        {...register('check', {
                            required: true,
                            validate: check => check === watch('password'),
                        })}
                    />
                </Row>
                {!errors.password && errors.check ? <ErrorMsg> 비밀번호가 일치하지 않습니다 </ErrorMsg> : <Msg>ㅤ</Msg>}
            </Column>
            <Column>
                <Row>
                    <Icon
                        icon={faUserSecret}
                        color={errors.secretMessage ? (theme.colors.red) : (theme.colors.white)}
                    />
                    <Input
                        placeholder='암호 메세지'
                        {...register('secretMessage', {
                            required: '암호 메세지를 작성해주세요',
                            minLength: { value: 3, message: '암호 메세지는 최소 3자 입니다' },
                            maxLength: { value: 20, message: '암호 메시지는 최대 20자 입니다' }
                        })}
                    />
                </Row>
                {errors.secretMessage ? 
                    <>
                        <ErrorMsg> {errors.secretMessage.message} </ErrorMsg>
                        <Msg>ㅤ</Msg>
                    </> :
                    <>
                        <Msg> 암호 메세지는 나중에 비밀번호 찾기에 사용됩니다 </Msg>
                        <Msg> 자신만의 개성 넘치는 단어로 설정해보세요! </Msg>
                    </>
                }
            </Column>
                <SubmitButton type='submit'>
                    확인
                </SubmitButton>
        </Box>
        </form>
    );
});

export default SignUpBox;

const Box = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 600px;
    height: 800px;
    animation: ${zoomIn} .6s ease;
    @media only screen and (max-width: 600px) {
        height: 450px;
    };
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-bottom: 10px;
`;

const Row = styled.div`
    position: relative;
    width: 420px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 30px;
    padding-bottom: 10px;
    border-bottom: 1px solid ${({ theme }) => theme.colors.white};
    @media only screen and (max-width: 600px) {
        width: 320px;
        margin-bottom: 20px;
    };
`;

const Input = styled.input`
    all: unset;
    width: 350px;
    height: 40px;
    font-size: 21px;
    padding-left: 20px;
    color: ${({ theme }) => theme.colors.white};
    @media only screen and (max-width: 600px) {
        width: 260px;
        font-size: 17px;
    };
`;

const SubmitButton = styled.button`
    all: unset;
    width: 200px;
    height: 85px;
    border-radius: 40px;
    background-color: ${({ theme }) => theme.colors.mainBlue};
    color: ${({ theme }) => theme.colors.white};
    font-size: 20px;
    text-align: center;
    margin-top: 20px;
    cursor: pointer;
    transition: .3s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white};
        color: ${({ theme }) => theme.colors.mainBlue};
    };
    @media only screen and (max-width: 600px) {
        width: 170px;
        height: 45px;
        font-size: 17px;
    };
`;

const CheckAccountButton = styled(SubmitButton)`
    position: absolute;
    right: 0px;
    top: 5px;
    width: 90px;
    height: 40px;
    font-size: 19px;
    margin-bottom: 10px;
    margin-top: 0;
    @media only screen and (max-width: 600px) {
