import React from 'react';
import styled from 'styled-components';

interface IMessageBox {
    nickName: string;
    message: string;
    socketID: boolean;
};

const MessageBox: React.FC<IMessageBox>= ({ nickName, message, socketID }) => {
    return (
        <Row fd={socketID ? 'row-reverse' : 'row'} >
            {socketID ?  
                <OwnChatName> { nickName } </OwnChatName> :
                <OthersName> { nickName }</OthersName>
            }
            <Balloon>
                <Tail />
                {message}
            </Balloon>
        </Row>
    );
};

export default MessageBox;
// 내가보내면
// fd: row-reverse
// 아니면 row

const Row = styled.div<{fd: string}>`
    position: relative;
    display: flex;
    flex-direction: ${({ fd }) => fd};
    width: 100%;
    min-height: 60px;
    margin-bottom: 10px;
`;

const Balloon = styled.div`
    position: relative;
    min-width: 100px;
    max-width: 200px;
    padding: 20px;
    margin-top: 29px;
    margin-right: 10px;
    right: 20px;
    min-height: 50px;
    word-break: break-all;
	background: ${({ theme }) => theme.colors.main60};
    color: ${({ theme }) => theme.colors.black};
    font-size: 17px;
	border-radius: 15px;
`;

const Tail = styled.div`
	position: absolute;
	right: 0;
	top: 50%;
	width: 0;
	height: 0;
	border: 18px solid transparent;
	border-left-color: ${({ theme }) => theme.colors.main60};
	border-right: 0;
	border-top: 0;
	margin-top: -15px;
	margin-right: -18px;
`;
// true r 25, false left 25
const OwnChatName = styled.div`
    font-size: 14px;
    max-width: 100px;
    position: absolute;
    right: 35px;
    top: 10px;
`;

const OthersName = styled.div`
    font-size: 14px;
    max-width: 100px;
    position: absolute;
    left: 25px;
    top: 10px;
`;
