import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faLock, faUserAlt, faUserSecret } from '@fortawesome/free-solid-svg-icons';
import { useForm, SubmitHandler } from 'react-hook-form';
import { zoomIn } from '../../style/keyframes';
import { theme } from '../../style/theme';

export interface ISignUpData {
    account: string;
    secretMessage: string;
}
const FindPWBox: React.FC = () => {
    const { register, handleSubmit, watch, setError, trigger, formState: { errors } } = useForm<ISignUpData>();
    const [isAlready, setIsAlready] = useState<boolean>(false); // 계정 중복체크
    const signUpBtn = useRef<HTMLButtonElement>(null);

    // 엔터 누를시 버튼 클릭
    const entered = (e: React.KeyboardEvent): void => {
        if (e.key === 'Enter') {
            signUpBtn.current?.click();
        }
    }
        return (
            <Box>
                <Column>
                    <Row>
                        <Icon icon={faUserAlt}
                              color={errors.account ? (theme.colors.red) : (theme.colors.white)}
                              />
                        <Input placeholder='아이디'
                            {...register('account', {
                                required: '아이디를 입력해주세요',
                                pattern: { value: /^[a-zA-Z0-9]+$/, message: '영문과 숫자만을 조합하여 입력해주세요'},
                                minLength: { value: 5, message: '아이디가 너무 짧아요' },
                                maxLength: { value: 19, message: '아이디가 너무 길어요' },
                               })}
                               />
                    </Row>
                    {errors.account ? <ErrorMsg> {errors.account.message} </ErrorMsg> : 'ㅤ'}
                </Column>
            </Box>
        )
}
export default FindPWBox;

const Box = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 600px;
    height: 600px;
    animation: ${zoomIn} .6s ease;
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-bottom: 10px;
`;

const Row = styled.div`
    width: 420px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 30px;
    padding-bottom: 10px;
    border-bottom: 1px solid ${({ theme }) => theme.colors.white};
`;

const Input = styled.input`
    all: unset;
    width: 400px;
    height: 40px;
    font-size: 21px;
    padding-left: 20px;
    color: ${({ theme }) => theme.colors.white};
`;

const Button = styled.button`
    all: unset;
    width: 200px;
    height: 55px;
    border-radius: 40px;
    background-color: ${({ theme }) => theme.colors.mainBlue};
    color: ${({ theme }) => theme.colors.white};
    font-size: 20px;
    text-align: center;
    margin-top: 20px;
    margin-bottom: 50px;
    cursor: pointer;
    transition: .3s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white};
        color: ${({ theme }) => theme.colors.mainBlue};
    }
`;

const DupliBtn = styled(Button)`
    width: 170px;
    height: 45px;
    margin-bottom: 10px;
    margin-top: 0;
`;

const Icon = styled(FontAwesomeIcon)<{
    color: string
}>`
    font-size: 35px;
    transform: translateY(-5px);
    color: ${({ color }) => color};
`;

const ErrorMsg = styled.div`
    font-size: 15px;
    transform: translateY(-10px);
    color: ${({ theme }) => theme.colors.red};
`;

const Msg = styled(ErrorMsg)`
    color: ${({ theme }) => theme.colors.white};
`;
