import { faPen } from '@fortawesome/free-solid-svg-icons';
import React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

interface INickName {
    nickName: string;
}

const ChangeNameBox: React.FC = () => {

    const { register, handleSubmit, formState: { errors } } = useForm<INickName>();

    const onClick: SubmitHandler<INickName> = (nickName) => {
        alert(JSON.stringify(alert));
    }

    return (
        <Container>
            <Column>
                <Category> 닉네임 변경 </Category>
                <Text> 변경할 닉네임을 입력해주세요 </Text>
                <InputBox>
                    <Icon icon={faPen} />
                    <Input {...register('nickName', {
                        required: '변경할 닉네임을 입력해주세요',
                        pattern: { value: /^[a-zA-Z0-9]+$/, message: '영문과 숫자만을 조합하여 입력해주세요'},
                        maxLength: { value: 19, message: '닉네임이 너무 길어요' },
                    })} />
                </InputBox>
                <Button onClick={handleSubmit(onClick)} > 변경하기 </Button>
            </Column>
        </Container>
    )
}

export default ChangeNameBox;

const Container = styled.div`
    width: 100%;
    height: 300px;
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Category = styled.div`
    font-size: 25px;
    color: ${({ theme }) => theme.colors.white};
`;

const Text = styled(Category)`
    margin-top: 20px;
    font-size: 18px;
`;

const InputBox = styled.div`
    width: 350px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 15px;
    padding-bottom: 10px;
    border-bottom: 1px solid ${({ theme }) => theme.colors.white};
`;

const Input = styled.input.attrs({
    placeholder: '변경할 닉네임'
})`
    all: unset;
    width: 300px;
    height: 40px;
    font-size: 21px;
    margin-top: 20px;
    padding-left: 15px;
    color: ${({ theme }) => theme.colors.white};
`;

const Button = styled.button`
    all: unset;
    width: 200px;
    height: 65px;
    border-radius: 40px;
    background-color: ${({ theme }) => theme.colors.mainBlue};
    color: ${({ theme }) => theme.colors.white};
    font-size: 20px;
    text-align: center;
    margin-top: 30px;
    margin-bottom: 50px;
    cursor: pointer;
    transition: .4s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white};
        color: ${({ theme }) => theme.colors.mainBlue};
    }
`;

const Icon = styled(FontAwesomeIcon)`
    font-size: 35px;
    transform: translateY(-5px);
    color: ${({ color }) => color};
`;

const ErrorMsg = styled.div`
    font-size: 16px;
    color: ${({ theme }) => theme.colors.red};
`;
