import { observer } from 'mobx-react';
import React, { useState } from 'react';
import styled, { Keyframes } from 'styled-components';
import userStore from '../../store/userStore';
import { tabClose, tabOpen } from '../../style/keyframes';
import Category from './Category';
import UserInfo from './UserInfo';
import { DisplayType } from '../../type';

const SideBar: React.FC = observer(() => {
    const [state, setState] = useState<boolean>(true);

    return (
        <Bar visible={userStore.isSignIn ? state ? 'block' : 'none' : 'block'}
             animation={state ? tabOpen : tabClose}
             >
            <UserInfo />
            <Category />
            <ToggleBtn onClick={() => setState(!state)} >
                {`<`}
            </ToggleBtn>
        </Bar>
    )
});

export default SideBar;

const Bar = styled.div<{
    visible: DisplayType,
    animation: Keyframes,
}>`
    position: absolute;
    right: 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 17%;
    height: 99%;
    background-color: ${({ theme }) => theme.colors.black};
    border-radius: 5px;
    border-left: 1px solid ${({ theme }) => theme.colors.white};
    display: ${({ visible }) => visible};
    animation: ${({ animation }) => animation } .8s ease forwards;
    transition: 1s ease;
    padding: 10px;
    z-index: 1;
`;

 const ToggleBtn = styled.button`
    all: unset;
    position: absolute;
    width: 20px;
    height: 250px;
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px;
    background-color: ${({ theme }) => theme.colors.white};
    left: -20px;
    top: 35%;
    text-align: center;
    font-size: 20px;
    cursor: pointer;
 `;
