import React from 'react';
import styled from 'styled-components';
import Post from './Post';
import View from './View';
import { ICurrentPost, usePost } from '../../Hooks/usePost';
import { IComment } from './View';

const PostBox: React.FC = () => {

    const post = usePost();

    return (
        <>
            <ListBox>
                {post.sentPosts && post.sentPosts.length > 0 ? 
                    post.sentPosts.map((x, i) => (
                        <Post nickName={x.nickName}
                              content={x.content}
                              key={i}
                              onClick={() => post.handleSentOne(x._id)}
                              />
                    )) :
                    <Ment>
                        아직 보낸 편지가 없어요
                    </Ment>
                }
            </ListBox>
            <View />
        </>
    )
}

export default PostBox;

const ListBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 30%;
    height: 740px;
    min-height: 740px;
    max-height: 740px;
    margin-top: 10px;
    border-radius: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.white10};
`;

const Ment = styled.div`
    color: ${({ theme }) => theme.colors.white};
    font-size: 18px;
    text-align: center;
    margin-top: 50px;
`;
