import React, { useEffect } from 'react'
import styled from 'styled-components';
import CenterView from '../components/publicComponents/CenterView'
import Container from '../components/publicComponents/Container'
import { useChat } from '../Hooks/useChat';
import MatchHandler from '../components/ChatPage/MatchHandler';
import ChatWindow from '../components/ChatPage/ChatWindow';
import { observer } from 'mobx-react';

const ChatPage: React.FC = observer(() => {
    const chat = useChat();

    useEffect(() => {
        console.log(chat.isSearching);
        console.log(chat.isMatched);
        console.log(chat.isFinished);
    }, [chat.isSearching, chat.isMatched, chat.isFinished]);

    return (
        <Container>
            <CenterView>
                <ChatBox>
                    <ChatWindow />
                    <HandlerBox>
                        <MatchHandler isSearching={chat.isSearching} 
                            isMatched={chat.isMatched}
                            isFinished={chat.isFinished}
                        />
                    </HandlerBox>
                </ChatBox>
            </CenterView>
        </Container>
    );
});

export default ChatPage;

const ChatBox = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    @media only screen and (max-width: 1450px) {
        flex-direction: column;
    };
    @media only screen and (max-width: 600px) {
        flex-direction: column-reverse;
    };
`;

const HandlerBox = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    min-width: 300px;
    height: 740px;
    border-radius: 10px;
    background-color: ${({ theme }) => theme.colors.main60};
    @media only screen and (max-width: 1450px) {
        height: 70%;
    };
`;
