import { useEffect } from 'react';
import { useLocation } from 'react-router';
import { useChat } from '../../Hooks/useChat';

export const chatDisconnect = () => {
    const chat = useChat();
    const pathname = useLocation();

    useEffect(() => {
        chat.disconnect();
        console.log(pathname);
    }, [pathname]);

    return;
}
