import React from 'react';
import styled from 'styled-components';
import { TEXTLOGO_URL } from '../../config';
import { arrowPop, fadeIn } from '../../style/keyframes';

const Logo: React.FC = () => {

    return (
        <LogoBox>
            <div/>
            <LandingText
                data-aos='fade-up'
                data-aos-duration='1000'
            >
                セレンディと音楽が楽しくなる！
            </LandingText>
        </LogoBox>
    );
};

export default Logo;

const LogoBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    height: 100vh;
    padding-bottom: 50px;
    margin-bottom: 300px;
`;

const LandingText = styled.div`
    font-size: 50px;
    @media only screen and (max-width: 500px) {
        font-size: 20px;
    }
`;

const StartBtn = styled.button`
    all: unset;
    height: 70px;
    border-radius: 40px;
    padding-left: 30px;
    padding-right: 30px;
    background-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
    font-size: 24px;
    text-align: center;
    margin-bottom: 50px;
    cursor: pointer;
    transition: .3s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.primaryWithOpacity};
        color: ${({ theme }) => theme.colors.black};
    };
    @media only screen and (max-width: 500px) {
        width: 120px;
        height: 50px;
        font-size: 17px;
    }
`;
