import React from 'react';
import styled from 'styled-components';

interface IMessageBox {
    nickName: string;
    message: string;
    socketID?: string;
};

const MessageBox: React.FC<IMessageBox>= ({ nickName, message, socketID }) => {
    return (
        <Row fd='row-reverse' >
            <Balloon>
                <Tail />
                <NickName> {nickName} </NickName>
                {message}
            </Balloon>
        </Row>
    )
}

export default MessageBox;
// 내가보내면
// fd: row-reverse
// 아니면 row

const Row = styled.div<{fd: string}>`
    display: flex;
    flex-direction: ${({ fd }) => fd};
    width: 100%;
    min-height: 60px;
    margin-bottom: 10px;
`;



const Balloon = styled.div`
    position: relative;
    padding: 20px;
    min-width: 100px;
    margin-top: 30px;
    right: 20px;
    height: 80px;
	background: ${({ theme }) => theme.colors.main60};
    color: ${({ theme }) => theme.colors.black};
    font-size: 17px;
	border-radius: 15px;
`;

const Tail = styled.div`
	position: absolute;
	right: 0;
	top: 50%;
	width: 0;
	height: 0;
	border: 18px solid transparent;
	border-left-color: ${({ theme }) => theme.colors.main60};
	border-right: 0;
	border-top: 0;
	margin-top: -19px;
	margin-right: -18px;
`;

const NickName = styled.div`
    font-size: 14px;
    min-width: 100px;
    max-width: 120px;
    position: absolute;
    top: -20px;
    right: -5px;
`;
