import React, { useEffect } from 'react';
import styled from 'styled-components';
import { usePost } from '../../Hooks/usePost';
import Post from './Post';

const SelectBox: React.FC = () => {
    const post = usePost();

    useEffect(() => {
        post.getSentPosts();
    }, []); //eslint-disable-line

    return (
        <ListBox>
            {post.sentPosts.length > 0 ? 
                post.sentPosts.map(x => (
                    x.posts.map((x: any) => (
                        <Post fromAccount={x.fromAccount}
                              content={x.content}
                              />
                    ))
                )) :
                <div>
                    아직 보낸 편지가 없어요! 상대방에게 편지를 보내보세요
                </div>
            }
        </ListBox>
    )
}

export default SelectBox;

const ListBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 55%;
    height: 670px;
    border-radius: 10px;
    background-color: ${({ theme }) => theme.colors.white20};
`;
