import { observer } from 'mobx-react';
import React, { useEffect } from 'react'
import authStore from '../store/authStore';
import { useHistorys } from '../Hooks/useHistorys';
import Container from '../components/publicComponents/Container';
import styled, { Keyframes } from 'styled-components';
import Manual from '../components/Main/Manual';
import { slideUp, slideOut } from '../style/keyframes';

const Main: React.FC = observer(() => {

    const history = useHistorys();

    const pushUser = (userStatus: boolean): void => {
        if (userStatus) {
            return;
        } else {
            history.pushStart();
        }
    }

    useEffect(() => {
        pushUser(authStore.isSignIn);
    }, [authStore.isSignIn]); // eslint-disable-line

    return (
        <Container>
            <Screen animation={history.currentPage === 'main' ? slideUp : slideOut} >
                <Manual />
            </Screen>
        </Container>
    );
});

export default Main;

const Screen = styled.div<{ animation: Keyframes }>`
    position: absolute;
    left: 10px;
    bottom: 18px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 80.5%;
    min-width: 300px;
    /* height: 792px; */
    height: 81.5%;
    border-radius: 30px;
    padding: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.black20};
    animation: ${({ animation }) => animation} .5s ease;
    transition: 1s ease;
    z-index: 1;
    @media only screen and (max-width: 500px) {
        width: 96%;
    }
`;
