import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock, faUserAlt } from '@fortawesome/free-solid-svg-icons';
import { useForm, SubmitHandler } from 'react-hook-form';
import { theme } from '../../style/theme';
import { usePush } from '../../hook/usePush';
import userStore from '../../store/userStore';
import { observer } from 'mobx-react';
import Loader from 'react-loader-spinner';
import { ISignInData } from '../../interfaces';
import AccountBox from '../PublicComponents/AccountBox';

const SignInBox: React.FC = observer(() => {
    const { register, handleSubmit, setError, formState: { errors } } = useForm<ISignInData>();
    const main = usePush('main');
    const findPW = usePush('findpw');
    const signUp = usePush('signup');

    const setErrors = (): void => {
        setError('account', {
            type: 'invalid',
            message: 'ㅤ'
        });
        setError('password', {
            type: 'invalid',
            message: '아이디나 비밀번호를 다시 확인해주세요',
        });
    };

    const onSubmit: SubmitHandler<ISignInData> = async (data) => {
        const isSuccess = await userStore.signIn(data);
        if (isSuccess) {
            main.push();
        } else {
            setErrors();
        };
    };

    return (
        <>
            {!userStore.isLogging ? 
                <form onSubmit={handleSubmit(onSubmit)}>
                    <AccountBox>
                    <Column>
                        <Row>
                            <Icon
                                icon={faUserAlt}
                                color={errors.account ? (theme.colors.red) : (theme.colors.white)}
                            />
                            <Input
                                placeholder='아이디'
                                {...register('account', {
                                    required: '아이디를 입력해주세요',
                                    pattern: { value: /^[a-zA-Z0-9]+$/, message: '영문과 숫자만을 조합하여 입력해주세요'},
                                    minLength: { value: 5, message: '아이디가 너무 짧아요' },
                                    maxLength: { value: 19, message: '아이디가 너무 길어요' },
                                })}
                            />
                        </Row>
                        {errors.account ? <ErrorMsg> {errors.account.message} </ErrorMsg> : 'ㅤ'}
                    </Column>

                    <Column>
                        <Row>
                            <Icon
                                icon={faLock}
                                color={errors.password ? (theme.colors.red) : (theme.colors.white)}
                            />
                            <Input 
                                placeholder='비밀번호'
                                autoComplete='off'
                                type='password'
                                {...register('password', {
                                    required: '비밀번호를 입력해주세요',
                                })}
                            />
                        </Row>
                        {errors.password ? <ErrorMsg> {errors.password.message} </ErrorMsg> : 'ㅤ'}
                    </Column>
                    <ButtonBox>
                        <SubmitButton type='submit'>
                            로그인
                        </SubmitButton>
                        <SubmitButton onClick={signUp.push}>회원가입</SubmitButton>
                    </ButtonBox>
                        <ForgotPW onClick={findPW.push} >
                            비밀번호가 기억이 안나시나요?
                        </ForgotPW>
                    </AccountBox> 
                </form> : 
                <Loader type="Circles"
                    color={theme.colors.plum}
                    height={60}
                    width={60}
                />
            }
        </>
    );
});

export default SignInBox;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-bottom: 30px;
`;

const Row = styled.div`
    width: 420px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    margin-bottom: 15px;
    padding-bottom: 10px;
    border-bottom: 1px solid ${({ theme }) => theme.colors.white};
    @media only screen and (max-width: 600px) {
        width: 320px;
    };
`;

const ButtonBox = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 460px;
    @media only screen and (max-width: 600px) {
        width: 320px;
    };
`;

const Input = styled.input`
    all: unset;
    width: 350px;
    height: 40px;
    font-size: 21px;
    padding-left: 20px;
    color: ${({ theme }) => theme.colors.white};
    @media only screen and (max-width: 600px) {
        width: 260px;
        font-size: 17px;
    };
`;

const SubmitButton = styled.button`
    all: unset;
    width: 200px;
    height: 65px;
    border-radius: 40px;
    background-color: ${({ theme }) => theme.colors.mainBlue};
    color: ${({ theme }) => theme.colors.white};
    font-size: 20px;
    text-align: center;
    margin-top: 30px;
    margin-bottom: 50px;
    cursor: pointer;
    transition: .4s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white};
        color: ${({ theme }) => theme.colors.mainBlue};
    };
    @media only screen and (max-width: 600px) {
        width: 120px;
        height: 45px;
        font-size: 17px;
    };
`;

const Icon = styled(FontAwesomeIcon)<{ color: string }>`
    font-size: 35px;
    color: ${({ color }) => color};
    @media only screen and (max-width: 600px) {
        font-size: 25px;
    };
`;

const ErrorMsg = styled.div`
    font-size: 16px;
    color: ${({ theme }) => theme.colors.red};
`;

const ForgotPW = styled.div`
    font-size: 16px;
    color: ${({ theme }) => theme.colors.white};
    cursor: pointer;
    &:hover {
        text-decoration: underline;
    };
`;
