import React from 'react';
import styled from 'styled-components';

const ChatWindow: React.FC = () => {
    return (
        <>
            <Screen />
            <SenderBox>
                <Input />
                <SendBtn>
                    전송
                </SendBtn>
            </SenderBox>
            <HandlerContainer>
                <Ment> adas </Ment>
            </HandlerContainer>
        </>
    );
};

export default ChatWindow;

const Screen = styled.div`
    position: absolute;
    top: 14px;
    left: 20px;
    width: 740px;
    min-width: 500px;
    height: 680px;
    background-color: ${({ theme }) => theme.colors.white20};
    border-radius: 30px;
    border: 1px solid ${({ theme }) => theme.colors.white};
`;

const SenderBox = styled.div`
    position: absolute;
    bottom: 14px;
    left: 20px;
    width: 740px;
    min-width: 500px;
    height: 50px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const Input = styled.input`
    all: unset;
    width: 84%;
    min-height: 50px;
    max-height: 150px;
    padding-left: 30px;
    font-size: 20px;
    color: ${({ theme }) => theme.colors.black};
    background-color: ${({ theme }) => theme.colors.white20};
    border: 1px solid ${({ theme }) => theme.colors.white};
    border-radius: 30px;

`;

const SendBtn = styled.button`
    all: unset;
    width: 10%;
    height: 50px;
    font-size: 18px;
    text-align: center;
    border: 1px solid ${({ theme }) => theme.colors.white};
    background-color: ${({ theme }) => theme.colors.white20};
    border-radius: 30px;
    color: ${({ theme }) => theme.colors.white};
    cursor: pointer;
    transition: .3s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white};
        color: ${({ theme }) => theme.colors.black};
    }
`;

const HandlerContainer = styled.div`
    position: absolute;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    top: 14px;
    right: 15px;
    width: 560px;
    height: 740px;
    background-color: ${({ theme }) => theme.colors.white20};
    border: 1px solid ${({ theme }) => theme.colors.white};
    border-radius: 30px;
`;

const Ment = styled.div`
    margin-top: 40px;
    position: absolute;
    top: 14px;

`;
