import React from 'react';
import styled, { Keyframes } from 'styled-components';
import { useHistorys } from '../../Hooks/useHistorys';
import { slideUp, stop } from '../../style/keyframes';

const CenterView: React.FC = ({ children }) => {

    const history = useHistorys();

    return (
        <Screen animation={history.isFirstRun.current ? slideUp : stop} >
            {children}
        </Screen>
    )
}

export default CenterView;

const Screen = styled.div<{ animation: Keyframes }>`
    position: absolute;
    left: 10px;
    bottom: 18px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 80.5%;
    min-width: 300px;
    height: 81.5%;
    border-radius: 30px;
    padding: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.black20};
    animation: ${({ animation }) => animation};
    transition: 1s ease;
    z-index: 1;
    @media only screen and (max-width: 500px) {
        width: 96%;
    }
`;
