import { useState } from 'react';
import { useHistory } from 'react-router-dom';

type PageList = 'mypage' | 'main' | null;

export const useHistorys = () => {
    const history = useHistory();
    const [currentPage, setCurrentPage] = useState<PageList>(null);

    const pushStart = ():void => {
        history.push('/');
    };

    const pushMain = (): void => {
        setCurrentPage('main')
        history.push('/main');
    };

    const pushSignUp = (): void => {
        history.push('/signup');
    };

    const pushLogin = (): void => {
        history.push('/login');
    };

    const pushFindPW = (): void => {
        history.push('/findpw');
    };

    const pushMyPage = ():void => {
        setCurrentPage('mypage');
        // history.push('/mypage');
    }

    return {
        currentPage,
        pushStart,
        pushMain,
        pushSignUp,
        pushLogin,
        pushFindPW,
        pushMyPage,
    }
}
