import { useState } from 'react';
import { useHistory } from 'react-router-dom';

export const useHistorys = () => {
    const history = useHistory();
    const [isMainPage, setIsMainPage] = useState<boolean>(true);

    const pushStart = ():void => {
        history.push('/');
    };

    const pushMain = (): void => {
        history.push('/main');
        setTimeout(() => setIsMainPage(false), 1000);
        console.log(isMainPage);
    };

    const pushSignUp = (): void => {
        history.push('/signup');
    };

    const pushLogin = (): void => {
        history.push('/login');
    };

    const pushFindPW = (): void => {
        history.push('/findpw');
    };

    const pushMyPage = ():void => {
        history.push('/mypage');
    }

    return {
        isMainPage,
        setIsMainPage,
        pushStart,
        pushMain,
        pushSignUp,
        pushLogin,
        pushFindPW,
        pushMyPage,
    }
}
