import React from 'react';
import styled from 'styled-components';

const Blocks: React.FC = () => {
    return (
        <BlockContainer>
            <Block title='말해보세요' >
                주변 사람들에게 선뜻 말하기 힘든 고민이 있다면 <br/><br/>
                낯선 누군가에게 부담 없이 말해보세요
            </Block>
            <Space />
            <Block title='들어보세요' >
                낯선 누군가의 고민거리를 들어보세요 <br/><br/>
                들어주는 것만으로 상대방에게 힘이 될지도 몰라요
            </Block>
            <Space />
            <Block title='오늘은 어땠나요?' >
                고민이 아니더라도 소소한 일상을 공유해보세요<br/><br/>
            </Block>
        </BlockContainer>
    )
}

export default Blocks;

const Block: React.FC<{ title: string }>= ({ children, title }) => {
    return (
        <>
            <Text data-aos='fade-up'
                  data-aos-anchor-placement='center-center'
                  data-aos-duration='500'>
                {title}
            </Text>
            <Text size='28px'
                  data-aos='fade-up'
                  data-aos-anchor-placement='center-center'
                  data-aos-duration='900'>
                {children}
            </Text>
        </>
    );
}

const BlockContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 100%;
    height: 3000px;
`;

const Text = styled.div<{ size?: string, mb?: string }>`
    font-size: ${({ size }) => size || '50px'};
    color: ${({ theme }) => theme.colors.black};
    margin-bottom: ${({ mb }) => mb};
    text-align: center;
    margin-bottom: 50px;
`;

const Space = styled.div`
    width: 100%;
    height: 250px;
`;
