import React from 'react'
import styled from 'styled-components';
import { observer } from 'mobx-react';
import { TEXTLOGO_URL } from '../../config';
import authStore from '../../store/authStore';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClipboard, faDoorOpen } from '@fortawesome/free-solid-svg-icons';

type DisplayType = 'block' | 'none';

const Header: React.FC = observer(() => {
    return (
        <LogoBox visible={authStore.isSignIn ? 'block' : 'none'} >
            <Logo />
            <Buttons>
                <ButtonBox>
                    <Icon icon={faClipboard} iconsize='24px' />
                    <Text mt='8px' size='14px' > 내 정보 </Text>
                </ButtonBox>
                <ButtonBox onClick={() => authStore.signOut()} >
                    <Icon icon={faDoorOpen} iconsize='24px' />
                    <Text mt='8px' size='14px' > 로그아웃 </Text>
                </ButtonBox>                
            </Buttons>
        </LogoBox>
    );
});

export default Header;

const LogoBox = styled.div<{ visible: DisplayType }>`
    position: fixed;
    top: 10px;
    left: 10px;
    display: ${({ visible }) => visible};
    z-index: 1;
`

const Logo = styled.div`
    width: 350px;
    height: 120px;
    background-image: url(${TEXTLOGO_URL});
    background-size: cover;
    background-position: center;
`;

const ButtonBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-radius: 20px;
    min-width: 68px;
    padding: 10px;
    margin: 5px;
    transition: .3s ease;
    cursor: pointer;
    &:hover {
        background-color: ${({ theme }) => theme.colors.white50};
    }
`;

const Buttons = styled.div`
    position: absolute;
    top: 15px;
    left: 350px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`;

const Icon = styled(FontAwesomeIcon)<{ iconsize: string }>`
    font-size: ${({ iconsize }) => iconsize};
    color: ${({ theme }) => theme.colors.black};
`;

const Text = styled.span<{ 
    size?: string;
    color?: string;
    ml?: string;
    mt?: string;
}>`
    font-size: ${({ size }) => size};
    color: ${({ theme }) => theme.colors.black};
    margin-left: ${({ ml }) => ml};
    margin-top: ${({ mt }) => mt};
`;
