import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import { ICurrentPost } from '../../Hooks/usePost';

interface IView {
    currentSentPost: ICurrentPost | undefined
}

const View: React.FC<IView>= ({ currentSentPost }) => {
    return (
        <LETTER>
            {currentSentPost ?
                <>
                    <NickName> {currentSentPost.nickName}님의 편지 </NickName>
                    <Content>
                        {currentSentPost.content}
                    </Content>
                    <CommentBox>
                        {currentSentPost.comment ? 
                            <>
                                <Column>
                                    <Icon icon={faUser} />
                                    <UserName> {currentSentPost.comment.nickName} </UserName>
                                </Column>
                                <Comment> {currentSentPost.comment.content} </Comment>
                            </> :
                            <>
                                <Comment> 아직 답장이 오지 않았어요 </Comment>
                            </>
                        }
                    </CommentBox>
                </> :
                <>
                </>

            }
        </LETTER>
    )
}

export default View;

const LETTER = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 69%;
    height: 740px;
    min-height: 740px;
    max-height: 740px;
    margin-top: 10px;
    border-radius: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.white10};
`;

const NickName = styled.div`
    width: 100%;
    height: 50px;
    line-height: 50px;
    margin-top: 20px;
    font-size: 23px;
    text-align: center;
    color: ${({ theme }) => theme.colors.white};
`;

const Content = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 80%;
    min-height: 400px;
    font-size: 20px;
    white-space: pre;
    color: ${({ theme }) => theme.colors.white};
`;

const CommentBox = styled.div`
    position: absolute;
    bottom: 20px;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: flex-start;
    padding: 10px;
    border-radius: 10px;
    border: 1px solid ${({ theme }) => theme.colors.white};
    width: 80%;
    white-space: nowrap;
    overflow: auto;
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Icon = styled(FontAwesomeIcon)`
    font-size: 30px;
    color: ${({ theme }) => theme.colors.white};
`;

const UserName = styled.div`
    font-size: 16px;
    text-align: center;
    margin-top: 10px;
    color: ${({ theme }) => theme.colors.white};
`;

const Comment = styled.div`
    margin-left: 20px;
    font-size: 18px;
    color: ${({ theme }) => theme.colors.white};
`;
