import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock, faUnlock, faUserAlt } from '@fortawesome/free-solid-svg-icons';
import { useForm } from 'react-hook-form';
import { theme } from '../../style/theme';
import { useHistorys } from '../../Hooks/useHistorys';

interface Inputs {
    account: string;
    password: string;
    check: string;
}
const SignUpBox: React.FC = () => {
    const { register, handleSubmit, watch, formState: { errors } } = useForm<Inputs>();
    const history = useHistorys();

    return (
        <Box>
            <div style={{ fontSize: '24px', marginBottom: '25px' }} > 사용하고 싶은 아이디와 비밀번호를 입력해주세요 </div>
            <Column>
                <Row>
                    <Icon icon={faUserAlt}
                          color={errors.account ? (theme.colors.red) : (theme.colors.black)}
                          />
                    <Input {...register('account', {
                        required: '아이디를 입력해주세요',
                        pattern: { value: /^[a-zA-Z0-9]+$/, message: '영문과 숫자만을 조합하여 입력해주세요'},
                        minLength: { value: 5, message: '아이디는 최소 5자 이상입니다' },
                        maxLength: { value: 17, message: '아이디는 최대 17자 입니다' },
                    })}
                    placeholder=''
                    />
                </Row>
                {errors.account ? <ErrorMsg> {errors.account.message} </ErrorMsg> : 'ㅤ'}
            </Column>

            <Column>
                <Row>
                    <Icon icon={faLock}
                          color={errors.password ? (theme.colors.red) : (theme.colors.black)}
                          />
                    <Input {...register('password', {
                       required: '비밀번호를 입력해주세요',
                    })}
                       type='password'
                    />
                </Row>
                {errors.password ? <ErrorMsg> {errors.password.message} </ErrorMsg> : 'ㅤ'}
            </Column>
            <Column>
                <Row>
                    <Icon icon={faUnlock}
                          color={errors.password ? (theme.colors.red) : (theme.colors.black)}
                          />
                    <Input {...register('check', {
                        required: true,
                        validate: check => check === watch('password'),
                    })}
                       type='password'
                    />
                </Row>
                {errors.check ? <ErrorMsg> 비밀번호가 일치하지 않습니다 </ErrorMsg> : 'ㅤ'}
            </Column>
            <ButtonBox>
                <Button onClick={history.pushSignUp} > 회원가입 </Button>
                <Button> 로그인 </Button>
            </ButtonBox>
        </Box>
    )
}

export default SignUpBox;

const Box = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 600px;
    height: 600px;
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Row = styled.div`
    width: 600px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
`;

const ButtonBox = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    width: 460px;
`;

const Input = styled.input`
    all: unset;
    width: 400px;
    height: 60px;
    font-size: 23px;
    color: ${({ theme }) => theme.colors.black};
    border-bottom: 1px solid ${({ theme }) => theme.colors.black};
    padding-left: 25px;
    padding-top: 5px;
    padding-bottom: 5px;
    margin-bottom: 20px;
`;

const Button = styled.button`
    all: unset;
    width: 200px;
    height: 65px;
    border-radius: 40px;
    background-color: ${({ theme }) => theme.colors.mainBlue};
    color: ${({ theme }) => theme.colors.white};
    font-size: 20px;
    text-align: center;
    margin-top: 30px;
    margin-bottom: 50px;
    cursor: pointer;
`;

const Icon = styled(FontAwesomeIcon)<{ color: string }>`
    font-size: 35px;
    height: 52px;
    padding-bottom: 20px;
    border-bottom: 1px solid ${({ theme }) => theme.colors.black};
    color: ${({ color }) => color}
`;

const ErrorMsg = styled.div`
    font-size: 16px;
    color: ${({ theme }) => theme.colors.red};
`;
