import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';

const View: React.FC = () => {
    return (
        <LETTER>
            <ToAccount> ~에게 보낸편지 </ToAccount>
            <Content>
                대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음대충 편지내용 써져있음
            </Content>
            <CommentBox>
                <Column>
                    <Icon icon={faUser} />
                    <UserName> 유저 누구누구 </UserName>
                </Column>
                <Comment> 대충 코멘트 적혀있음 </Comment>
            </CommentBox>
        </LETTER>
    )
}

export default View;

const LETTER = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 69%;
    height: 740px;
    min-height: 740px;
    max-height: 740px;
    margin-top: 10px;
    border-radius: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.white20};
`;

const ToAccount = styled.div`
    width: 100%;
    height: 50px;
    line-height: 50px;
    font-size: 28px;
    text-align: center;
    color: ${({ theme }) => theme.colors.white};
`;

const Content = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 80%;
    min-height: 400px;
    font-size: 20px;
    color: ${({ theme }) => theme.colors.white};
`;

const CommentBox = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
`;

const Row = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Icon = styled(FontAwesomeIcon)`
    font-size: 30px;
    color: ${({ theme }) => theme.colors.white};
`;

const UserName = styled.div`
    font-size: 17px;
    text-align: center;
    margin-top: 10px;
    color: ${({ theme }) => theme.colors.white};
`;

const Comment = styled(UserName)``;
