import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane, faComments, faUsers, faPlus, faEquals} from '@fortawesome/free-solid-svg-icons';
import { WHALE } from '../../config';
import { whalePop, zoomIn } from '../../style/keyframes';

const Manual: React.FC = () => {

    return (
        <Column>
            <LogoBox>
            <Logo />
            </LogoBox>
                <Box>
                    <Icon icon={faPaperPlane} />
                    <Icon icon={faPlus} />
                    <Icon icon={faComments} />
                    <Icon icon={faEquals} />
                    <Icon icon={faUsers} />
                </Box>
        </Column>
    );
};

export default Manual;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    height: 100%;
    animation: ${zoomIn} .8s
`;

const Box = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
    width: 400px;
    margin-top: 20px;
    @media only screen and (max-width: 600px) {
        justify-content: center;
    };
`;

const Icon = styled(FontAwesomeIcon)`
    color: ${({ theme }) => theme.colors.white};
    margin-right: 20px;
    font-size: 50px;
    @media only screen and (max-width: 600px) {
        font-size: 30px;
    };
`;

const LogoBox = styled.div`
    width: 500px;
    height: 500px;
    display: flex;
    flex-direction: center;
    align-items: center;
    margin-bottom: 50px;
    @media only screen and (max-width: 600px) {
        width: 250px;
        height: 250px;
    };
`;


const Logo = styled.div`
    width: 500px;
    min-width: 500px;
    height: 500px;
    margin-right: 150px;
    background-image: url(${WHALE});
    background-size: cover;
    background-position: center;
    animation: ${whalePop} .5s ease-out alternate infinite;
    @media only screen and (max-width: 600px) {
        animation: none;
        max-width: 250px;
        height: 250px;
    };
`;
