import { useState, useRef } from 'react'
import { VisibilityType, IRecentChat } from '../interfaces';
import userStore from '../store/userStore';


export const useChat = () => {
    const [isSearching, setIsSearching] = useState<boolean>(true);
    const [isMatched, setIsMatched] = useState<boolean>(false);
    const [isFinished, setIsFinished] = useState<boolean>(false);
    const [display, setDisplay] = useState<VisibilityType>('hidden');
    const [chatLog, setChatLog] = useState<IRecentChat[]>([]);
    const [recentChat, setRecentChat] = useState<IRecentChat>({ nickName: '', message: '', socketID: '' });
    const sendBtn = useRef<HTMLButtonElement>(null);
    const input = useRef<HTMLInputElement>(null);
    const screen = useRef<HTMLDivElement>(null);

    const handleEnter = (e: React.KeyboardEvent): void => {
        if (e.key === 'Enter') {
            sendBtn.current?.click();
        };
    };

    const handleCancel = (): void => {
        userStore.userSocket?.emit('cancel');
    };

    const handleFind = (): void => {
        userStore.userSocket?.emit('find');
    };

    const handleSearch = (): void => {
        console.log(isSearching);
        if (!isSearching) {
            setIsSearching(true);
            handleFind();
        } else { // 도중 취소
            setIsSearching(false);
            handleCancel();
        };
    };

    const getMatchedUser = (): void => {
        userStore.userSocket?.on('matched', () => {
            setIsMatched(true);
            setDisplay('visible');
            setChatLog([{
                nickName: 'SERENDY',
                message: '상대와 대화가 시작되었어요!',
                socketID: 'admin',
            }]);
        });
    };

    const handleSendMsg = (): void => {
        if (input.current?.value.length !== 0) {
            const nickName = userStore.user?.nickName;
            const message = input.current?.value;
            const data = {
                nickName: nickName,
                message: message,
            };
            userStore.userSocket?.emit('chat', data);
            input.current!.value = '';

        } else {
            return;
        };
    };

    const scrollToBottom = (): void => {
        if (screen.current?.scrollHeight && screen.current?.clientHeight) {
            const scroll = screen.current.scrollHeight - screen.current.clientHeight;
            screen.current?.scrollTo(0, scroll);
        } else {
            return;
        };
    };

    const handleReceiveMsg = () => {
        userStore.userSocket?.on('receive', (data, socketID)=> {
            setRecentChat({
                nickName: data.nickName,
                message: data.message,
                socketID: socketID,
            });
        });
    };

    const handlePushChat = (): void => {
        recentChat.message.length > 0 && setChatLog([...chatLog, recentChat]);
    };

    const handleFinished = () => {
        setIsFinished(true);
        setRecentChat({
            nickName: 'SERENDY',
            message: '대화가 종료되었어요!',
            socketID: 'admin',
        });
    };

    const stopChat = (): void => {
        userStore.userSocket?.emit('stop chat');
        handleFinished();
    };

    const chatStopped = (): void => {
        userStore.userSocket?.on('is ended', () => {
            handleFinished();
        });
    };

    const reStart = (): void => {
        setChatLog([]);
        setIsSearching(false);
        setIsFinished(false);
        setIsMatched(false);
        input.current!.value = '';
    };

    return {
        isSearching,
        isMatched,
        display,
        sendBtn,
        input,
        screen,
        chatLog,
        recentChat,
        isFinished,
        setRecentChat,
        setChatLog,
        handleEnter,
        handleSendMsg,
        handleSearch,
        handleReceiveMsg,
        handlePushChat,
        getMatchedUser,
        stopChat,
        setIsFinished,
        reStart,
        chatStopped,
        scrollToBottom,
    };
};
