import React, { useCallback } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClipboard, faDoorOpen, faUserAlt } from '@fortawesome/free-solid-svg-icons';
import userStore from '../../store/userStore';
import { observer } from 'mobx-react';
import { DisplayType } from '../../interfaces/index';
import { usePush } from '../../hook/usePush';

const pushList = [
    {
        path: 'main',
    },
    {
        path: '', // default
    },
];

const UserInfo: React.FC = observer(() => {

    const histories = pushList.map(x => (
        usePush(x.path)
    ));


    const signOut = useCallback(() => {
        const isSuccess = userStore.signOut();
        if (isSuccess) histories[1].push();
    }, []);

    return (
        <Container>
            <Row style={{ marginTop: '10px' }} >
                <UserIcon icon={faUserAlt} iconsize='24px' />
                <Text ml='7px' size='16px' > {userStore.user?.nickName} </Text>
            </Row>
            <Row>
                <Box onClick={histories[0].push} >
                    <Icon icon={faClipboard} iconsize='24px' />
                    <Text mt='8px' size='14px' > 정보변경 </Text>
                </Box>
                <Box onClick={signOut} >
                    <Icon icon={faDoorOpen} iconsize='24px' />
                    <Text mt='8px' size='14px' > 로그아웃 </Text>
                </Box>
            </Row>
        </Container>
    );
});

export default UserInfo;

const Row = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    @media only screen and (max-width: 1450px) {
        display: grid;
        grid-template-areas:
        'a'
        'b';
    };
    @media only screen and (max-width: 600px) {
        margin-top: 8px;
    };
`;

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    width: 100%;
    height: 15%;
    min-width: 40px;
    min-height: 120px;
    @media only screen and (max-width: 600px) {
        font-size: 15px;
        margin-top: 50px;
    };
`;

const Icon = styled(FontAwesomeIcon)<{ iconsize: string, visible?: DisplayType }>`
    font-size: ${({ iconsize }) => iconsize};
    color: ${({ theme }) => theme.colors.white};
    display: ${({ visible }) => visible};
`;

const UserIcon = styled(Icon)`
`;

interface IText {
    size?: string;
    color?: string;
    ml?: string;
    mt?: string;
};

const Text = styled.span<IText>`
    font-size: ${({ size }) => size};
    color: ${({ theme }) => theme.colors.white};
    margin-left: ${({ ml }) => ml};
    margin-top: ${({ mt }) => mt};
    white-space: nowrap;
    @media only screen and (max-width: 1450px) {
        display: none;
    };
`;

const Box = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-radius: 10px;
    min-width: 40px;
    padding: 5px;
    margin: 5px;
    transition: .3s ease;
    cursor: pointer;
    &:hover {
        background-color: ${({ theme }) => theme.colors.main60};
    };
    @media only screen and (max-width: 600px) {
        min-width: 40px;
        margin-top: 10px;
    };
`;
