import React from 'react';
import styled from 'styled-components';
import { IMAGE1, IMAGE2, IMAGE3 } from '../../config';

const landingText: String[] = [
    'セレンディとメンバー募集を！',
    'セレンディとスタジオも！',
    'セレンディとメンバー募集を！',
    'セレンディとメンバー募集を！',
    'セレンディとメンバー募集を！',
];

const Logo: React.FC = () => {

    return (
            <>
        <FlexBox>
            <div style={{ marginTop: '160px'}} />
            <LandingText>
                セレンディとメンバー募集を！
            </LandingText>
            <CaptureImage imagePath={IMAGE1} />
        </FlexBox>
        <FlexBox>
            <LandingText>
                セレンディとスタジオも！
            </LandingText>
            <CaptureImage imagePath={IMAGE2} />
        </FlexBox>
        <FlexBox>
            <LandingText>
                チャットで連絡も！
            </LandingText>
            <CaptureImage imagePath={IMAGE3} />
        </FlexBox>
    </>
    );
};

export default Logo;

const FlexBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 120vh;
    padding-bottom: 50px;
    margin-bottom: 300px;
`;

const LandingText = styled.div`
    font-size: 50px;
    @media only screen and (max-width: 500px) {
        font-size: 20px;
    }
`;

const CaptureImage = styled.div<{imagePath: string}>`
    width: 375px;
    height: 812px;
    background-image: url(${({ imagePath}) => imagePath});
    background-size: cover;
    margin-top: 30px;
    background-position: center;
    @media only screen and (max-width: 500px) {
        width: 320px;
        height: 740px;
    }
`;

const StartBtn = styled.button`
    all: unset;
    height: 70px;
    border-radius: 40px;
    padding-left: 30px;
    padding-right: 30px;
    background-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
    font-size: 24px;
    text-align: center;
    margin-bottom: 50px;
    cursor: pointer;
    transition: .3s ease;
    &:hover {
        background-color: ${({ theme }) => theme.colors.primaryWithOpacity};
        color: ${({ theme }) => theme.colors.black};
    };
    @media only screen and (max-width: 500px) {
        width: 120px;
        height: 50px;
        font-size: 17px;
    }
`;
