import React, { useEffect } from 'react';
import styled from 'styled-components';
import PostBox from '../components/RecipientsPage/PostBox';
import CenterView from '../components/publicComponents/CenterView';
import Container from '../components/publicComponents/Container';
import { theme } from '../style/theme';
import Loader from 'react-loader-spinner';
import View from '../components/RecipientsPage/View';
import postStore from '../store/postStore';
import { usePost } from '../hook/usePost';
import { observer } from 'mobx-react';

const RecipientsPage: React.FC = observer(() => {
    const post = usePost();
    useEffect(() => {
        postStore.getReceivedPosts(post.handleIsLoading); 
    }, []); //eslint-disable-line

    return (
        <Container>
            <CenterView>
                    <Row>
                        {post.isLoading ?
                            <>
                                <PostBox receivedPosts={postStore.receivedPosts}
                                        showPost={postStore.handleReceivedOne}
                                        />                        
                                <View currentReceivedPost={postStore.currentReceivedPost}
                                    commentArea={post.commentArea}
                                    sendComment={() => postStore.sendComment(post.commentArea.current?.value)}
                                    />
                            </> :
                            <LoaderBox>
                                <Loader type="Circles"
                                        color={theme.colors.plum}
                                        height={60}
                                        width={60}
                                        />
                            </LoaderBox>
                        }
                    </Row>
            </CenterView>
        </Container>
    );
});

export default RecipientsPage;

const Row = styled.div`
    min-width: 100%;
    max-width: 100%;
    height: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`;

const LoaderBox = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
`;
