import { useState, useRef } from 'react'
import { VisibilityType } from '../type';
import userStore from '../store/userStore';

export const useChat = () => {
    const [isSearching, setIsSearching] = useState<boolean>(false);
    const [isMatched, setIsMatched] = useState<boolean>(false);
    const [display, setDisplay] = useState<VisibilityType>('hidden');
    const sendBtn = useRef<HTMLButtonElement>(null);
    const [inputMessage, setInputMessage] = useState<string>(''); // input target value

    const [messageHistory, setMessageHistory] = useState<string[]>(); // 메세지 내역을 담아두는 배열
    const [recentMessage, setRecentMessage] = useState<string>(''); // 서버에서 갱신된 메세지를 담는 state

    const handleInput = (e: React.ChangeEvent<HTMLInputElement>): void => {
        setInputMessage(e.target.value);
    }

    const handleEnter = (e: React.KeyboardEvent): void => {
        if (e.key === 'Enter') {
            sendBtn.current?.click();
        }
    }

    const showChat = (): void => {
        setDisplay('visible');
    }

    const handleBtn = (): void => {
        setIsSearching(!isSearching);
        setIsMatched(!isMatched); // 채팅개발하게되면 수정
    };

    const handleSearch = (): void => {
        if (display === 'hidden') {
            showChat();
            handleBtn();
        } else {
            handleBtn();
        }
    };

    const handleMatch = (): void => {
        setIsMatched(true);
    }

    const handleSendMsg = (): void => {
        if (inputMessage.length !== 0) {
            userStore.userSocket?.emit('chat', inputMessage);
            setInputMessage('');
        } else {
            return;
        }
    }



    return {
        isSearching,
        isMatched,
        inputMessage,
        display,
        sendBtn,
        messageHistory,
        recentMessage,
        handleInput,
        handleEnter,
        handleSendMsg,
        handleSearch,
        handleMatch,
        setMessageHistory,
        setRecentMessage,
    }
}
