import { action, computed, makeObservable, observable } from "mobx";
import serendyRepository from "../repository/serendyRepository";

interface IAccount extends IUser {
    account: string;
    password: string;
    secretMessage: string;
    nickname: string;
}

class AuthStore implements IAccount {

    private _account: string = '';
    private _password: string = '';
    private _secretMessage: string = '';
    private _isLogged: boolean = false;

    nickname: string = '';

    constructor() {
        makeObservable<AuthStore, '_account' | '_password' | '_isLogged' | '_secretMessage'>(this, {
            _account: observable,
            _password: observable,
            _isLogged: observable,
            _secretMessage: observable,
            account: computed,
            password: computed,
            secretMessage: computed,
            isLogged: computed,
            setAccount: action,
            setPassword: action,
        })
    }
    get account(): string {
        return this._account;
    };

    get password(): string {
        return this._password;
    };

    get secretMessage(): string {
        return this._secretMessage;
    }

    get isLogged(): boolean {
        return this._isLogged;
    }

    setAccount(account: string): void {
        this._account = account;
    };

    setPassword(password: string): void {
        this._password = password;
    };
}

const authStore = new AuthStore();
export default authStore;
