import React, { useEffect } from 'react';
import { useLocation } from 'react-router';
import { useChat } from '../../Hooks/useChat';

export const ChatDisconnect: React.FC = () => {
    const chat = useChat();
    const pathname = useLocation();

    useEffect(() => {
        chat.disconnect();
        console.log(pathname);
    }, [pathname]);

    return null;
}
