import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import GlobalStyle from './style/global';
import { useEffect } from 'react';
// PrivateRoute
import PrivateRoute from './components/publicComponents/PrivateRoute';
// theme
import { theme } from './style/theme';

import { ChatDisconnect } from './components/publicComponents/ChatDisconnect';
import Start from './pages/Start';
import Main from './pages/Main';
import SignIn from './pages/SignIn';
import SignUp from './pages/SignUp';
import SideBar from './components/SideBar/SideBar';
import Header from './components/Header/Header';
import MyPage from './pages/MyPage';
import ChatPage from './pages/ChatPage';
import PostPage from './pages/PostPage';
import authStore from './store/authStore';

function App() {

    useEffect(() => {
        authStore.signInWithToken();
    }, []);

    return (
        <>
            <ThemeProvider theme={theme} >
                <GlobalStyle />
                <BrowserRouter>
                    <Header />
                    <SideBar />
                    <Switch>
                        <Route exact path='/'>
                            <ChatDisconnect />
                            <Start />
                        </Route>
                        <Route exact path='/login'>
                            <ChatDisconnect />
                            <SignIn />
                        </Route>
                        <Route exact path='/signup'>
                            <ChatDisconnect />
                            <SignUp />
                        </Route>
                        <Route exact path='/findpw'>
                            <ChatDisconnect />
                            Find Password
                        </Route>
                        <PrivateRoute exact path='/main'>
                            <ChatDisconnect />
                            <Main />
                        </PrivateRoute>
                        <PrivateRoute exact path='/mypage'>
                            <ChatDisconnect />
                            <MyPage />
                        </PrivateRoute>
                        <PrivateRoute exact path='/chat'>
                            <ChatDisconnect />
                            <ChatPage />
                        </PrivateRoute>
                        <PrivateRoute exact path='/post'>
                            <ChatDisconnect />
                            <PostPage />
                        </PrivateRoute>
                    </Switch>
                </BrowserRouter>
            </ThemeProvider>
        </>
    );
}

export default App;
