import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-solid-svg-icons';

interface IView {
    nickName: string | undefined;
    content: string | undefined;
}

const View: React.FC<IView>= ({ nickName, content }) => {
    return (
        <LETTER>
            <NickName> 보낸사람 : {nickName} </NickName>
            <Content>
                {content}
            </Content>
            <CommentBox>
                <Column>
                    <Ment> 누구누구 님의 답장이 왔어요! </Ment>
                    <Icon icon={faUser} />
                    <UserName> 유저 누구누구 </UserName>
                </Column>
                <Comment> 대충 코멘트 적혀있음 </Comment>
            </CommentBox>
        </LETTER>
    )
}

export default View;

const LETTER = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    width: 69%;
    height: 740px;
    min-height: 740px;
    max-height: 740px;
    margin-top: 10px;
    border-radius: 10px;
    overflow: auto;
    background-color: ${({ theme }) => theme.colors.white20};
`;

const NickName = styled.div`
    width: 100%;
    height: 50px;
    line-height: 50px;
    margin-top: 20px;
    font-size: 28px;
    text-align: center;
    color: ${({ theme }) => theme.colors.white};
`;

const Content = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 80%;
    min-height: 400px;
    font-size: 20px;
    color: ${({ theme }) => theme.colors.white};
`;

const CommentBox = styled.div`
    position: absolute;
    bottom: 20px;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: flex-start;
    padding: 10px;
    border-radius: 10px;
    border: 1px solid ${({ theme }) => theme.colors.white};
    width: 80%;
`;

const Ment = styled.div`
    position: absolute;
    top: -50px;
    left: 40px;
    font-size: 23px;
    width: 300px;
    color: ${({ theme }) => theme.colors.white};
`;

const Column = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const Icon = styled(FontAwesomeIcon)`
    font-size: 30px;
    color: ${({ theme }) => theme.colors.white};
`;

const UserName = styled.div`
    font-size: 16px;
    text-align: center;
    margin-top: 10px;
    color: ${({ theme }) => theme.colors.white};
`;

const Comment = styled.div`
    margin-left: 20px;
    font-size: 18px;
    color: ${({ theme }) => theme.colors.white};
`;
