import { useRef, useState } from 'react'
import userStore from '../store/userStore';
import postRepository from '../repository/postRepository';

export interface ISendPost {
    account: string | null | undefined;
    nickName: string | null | undefined;
    content: string | undefined;
}

export interface IClickedPost {
    _id: string;
    toAccount: string;
    nickName: string;
    fromAccount: string;
    content: string;
}

export const usePost = () => {
    const [isSent, setIsSent] = useState<boolean>(false);
    const textArea = useRef<HTMLTextAreaElement>(null);
    const [sentPosts, setSentPosts] = useState<any[]>([]);
    const [receivedPosts, setReceivedPosts] = useState<any[]>([]);
    const [currentPost, setCurrentPost] = useState<IClickedPost>();

    const handlePost = async (): Promise<void> => {
        try {
            const data = {
                account: userStore.user?.account,
                nickName: userStore.user?.nickName,
                content: textArea.current?.value,
            }
            const response = await postRepository.sendPost(data);
            if ((response.data.message === 'Send Success')) {
                setIsSent(true);
            }
        } catch(err) {
            alert('서버가 점검중이에요');
        };
    };

    const getSentPosts = async (): Promise<void> => {
        try {
            const response = await postRepository.getSentPosts(userStore.user?.account);
            const posts = response.data;
            setSentPosts(posts);
        } catch(err) {
            console.log(err);
            return;
        }
    }

    const getReceivedPosts = async (): Promise<void> => {
        try {
            const response = await postRepository.getReceivePosts(userStore.user?.account);
            const posts = response.data;
            setReceivedPosts(posts);
        } catch(err) {
            console.log(err);
            return;
        }
    }

    const showSentOne = (_id: string, account: string): void => {
        console.log(_id);
        console.log(account);
        const post = sentPosts.flatMap((x, i) => (
            x.filter((x: any) => x._id === _id && x.account === account)
        ));
        console.log(post);
    }

    return {
        isSent,
        textArea,
        sentPosts,
        receivedPosts,
        handlePost,
        getSentPosts,
        getReceivedPosts,
        showSentOne,
    }
}
