import React from 'react'
import styled from 'styled-components';
import { observer } from 'mobx-react';
import { TEXTLOGO_URL } from '../../config';
import userStore from '../../store/userStore';
import { pushHistory } from '../../Hooks/pushHistory';

type DisplayType = 'block' | 'none';

const Header: React.FC = observer(() => {

    const history = pushHistory();

    return (
        <LogoBox visible={userStore.isSignIn ? 'block' : 'none'}
            onClick={history.pushMain}
        >
            <Logo />
        </LogoBox>
    );
});

export default Header;

const LogoBox = styled.div<{
    visible: DisplayType
}>`
    position: fixed;
    top: 5px;
    left: 20px;
    display: ${({ visible }) => visible};
    cursor: pointer;
    z-index: 1;
`;

const Logo = styled.div`
    width: 310px;
    height: 120px;
    background-image: url(${TEXTLOGO_URL});
    background-size: cover;
    background-position: center;
    @media only screen and (max-height: 700px) {
        width: 220px;
        height: 90px;
    };
    @media only screen and (max-width: 600px) {
        width: 150px;
        height: 60px;
    };
`;
